import 'dart:async';import 'package:covid19/pages/Provider.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'pages/home.dart';import 'pages/login.dart';void main() {  runApp(    ChangeNotifierProvider(      builder: (context) => MyProvider(),      child: MyApp(),    ),  );}class MyApp extends StatelessWidget {  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    return MaterialApp(      initialRoute: '/',      routes: {        // Cuando naveguemos hacia la ruta "/", crearemos el Widget FirstScreen        '/home': (context) => home(),      },      title: 'Flutter Demo',      theme: ThemeData(        primarySwatch: Colors.blue,        visualDensity: VisualDensity.adaptivePlatformDensity,      ),      home: MyHomePage(title: 'Emergencia covid19'),    );  }}class MyHomePage extends StatefulWidget {  MyHomePage({Key key, this.title}) : super(key: key);  final String title;  @override  _MyHomePageState createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  @override  void initState() {    super.initState();    Timer(        Duration(seconds: 5),            () => Navigator.of(context).pushReplacement(MaterialPageRoute(            builder: (BuildContext context) => login())));  }  @override  Widget build(BuildContext context) {    return Scaffold(      //backgroundColor: Color(0xFFf7f7f7),      body: Center(        child: Image.asset('images/msplogo.jpg', width: 250, height: 250),      ),    );  }}